/**
 * @description
 *
 * Interface que representa as informações do usuário
 * no Protheus TFace
 */
export interface ProUserInfo {
    id?: string;
    name?: Name;
    complete_name?: string;
    userName?: string;
    displayName?: string;
    meta?: Meta;
    externalId?: string;
    phoneNumbers?: Array<any>;
    emails?: Array<Email>;
    active?: boolean;
    groups?: Array<any>;
    roles?: Array<any>;
    title?: string;
    department?: string;
}
export interface Name {
    formatted: string;
    givenName: string;
    familyName: string;
}
export interface Meta {
    resourceType: string;
    created: string;
    lastModified: string;
}
export interface Email {
    value: string;
    type: string;
    primary: boolean;
}
export interface ProUserList {
    hasNext: boolean;
    schemas: Array<string>;
    items: Array<ProUserInfo>;
}
